import React from "react";
import { Link } from 'react-router-dom';
import styled from "styled-components";

const Header = styled.header`
color: white;
position: fixed;
top: 0;
left: 0;
width: 100%;
height: 50px;
display: flex;
align-items:center;
padding: 0 10px
background-color: rgba(20, 20, 20, 0.8)`;
const List = styled.ul`
display:flex;
&:hover{
    background-color: blue;
}
`;
const Item = styled.li``;

const SLink = styled(Link)``;

export default () => (
    <Header>
        <List>
            <Item>
                <SLink to={{pathname: "/"}}>Movies</SLink>
            </Item>
            <Item>
                <SLink to={{pathname: "/tv"}}>TV</SLink>
            </Item>
            <Item>
                <SLink to={{pathname: "/search"}}>Search</SLink>
            </Item>
        </List>
    </Header>
)
